#[component]
pub fn Shape(
    w: Option<String>, 
    h: Option<String>,
    model: ShapeModel,
    color_: Option<conf::Hex>
) -> Element {
    rsx!(
        svg {
            xmlns: "http://www.w3.org/2000/svg",
            width: w,
            height: h,
            view_box: format!("0 0 256 256"),
            preserve_aspect_ratio: "xMidYMid meet",
            fill: "none",
            path {
                fill: color_.unwrap_or_default().to_string(),
                d: model.to_string()
            }
        }
    )
}